    <!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Appreciation Letter</title>
{{--    <link rel="stylesheet" href="main.css">--}}
    <style>
        @import url("
        https://fonts.googleapis.com/css2?family=Indie+Flower&
        display=swap"
        );
        *, *:before, *:after {
            box-sizing: border-box;
        }
        body {
            min-height: 100vh;
            margin: 0;
            padding: 20px 0;
            display: flex;
            justify-content: center;
            color: #3600cc;
            font-family: "
            Indie Flower"
            , monospace;
            background-color: #1e232c;
            scroll-behavior: smooth;
        }
        #paper {
            width: 90vw;
            max-width: 600px;
            min-height: 80vh;
            position: relative;
            padding-top: 25px;
            background-color: white;
            background-image: repeating-linear-gradient(white 0px, white 24px, teal 25px);
            box-shadow: 0px 0px 5px 0px #888;
        }
        #paper:before {
            content: "
            "
        ;
            width: 2px;
            height: 100%;
            position: absolute;
            top: 0;
            left: 40px;
            background-color: rgba(255, 0, 0, 0.6);
        }
        #letter {
            padding-top: 6px;
            padding-left: 55px;
            padding-right: 16px;
            line-height: 25px;
            font-size: 19px;
            letter-spacing: 1px;
            word-spacing: 5px;
        }
        #letter span {
            display: inline-block;
            padding: 0 4px;
            background: rgba(114, 226, 9, 0.4);
            border-top-left-radius: 225px 20px;
            border-top-right-radius: 22px 218px;
            border-bottom-right-radius: 225px 15px;
            border-bottom-left-radius: 15px 255px;
        }
        #options {
            display: flex;
            flex-wrap: wrap;
            padding: 15px 15px 0 55px;
        }
        #options button {
            flex-grow: 1;
            padding: 6px 8px;
            margin: 0 6px 6px 0;
            background: transparent;
            font-family: "
            Indie Flower"
            , monospace;
            font-size: 18px;
            color: #3600cc;
            border: 2px solid #3600cc;
            border-top-left-radius: 255px 15px;
            border-top-right-radius: 15px 255px;
            border-bottom-right-radius: 125px 15px;
            border-bottom-left-radius: 5px 255px;
            cursor: pointer;
        }
    </style>
</head>
<body>
<div id="paper">
    <div id="letter"></div>

    <div id="options"></div>
</div>
</body>
<script>const letterEl = document.getElementById("letter")
    const optionsEl = document.getElementById("options")
    const speed = 80
    let sentenceID = 0

    const sentences = [
        {
            sentence: "Thank you all for your kind birthday wishes that helped make it a very, very happy birthday!",
            options: ["Friend ❤️", "Family ❤️"],
            postfix: "."
        },
        {
            sentence: "I just wanted to write a quick message to let you all know how much all of your kind words meant to me today.",
            options: ["And there's more"],
            postfix: ","
        },
        {
            sentence: "Honestly, your messages have touched me beyond words. In short, you have all just simply made my day!",
            options: ["Keep reading"],
            postfix: ","
        },
        {
            sentence: "My great big smile on the outside is proof of my gratitude, while on the inside my heart just keeps thanking you all!",
            options: ["And a important reminder"],
            postfix: ","
        },
        {
            sentence: "Thank you all so much for thinking of me on my birthday, and I am sending my biggest kisses and warmest hugs back to you all! Much love!",
            options: ["And to the final part"],
            postfix: "!"
        },
        {
            sentence: "❤️❤️❤️❤️❤️❤️❤️❤️❤️❤️❤️",
            options: ["Stay Blessed ❤️"],
            postfix: "!"
        },
    ];


    const writeLetter = () => {
        if (sentenceID < sentences.length) {
            input = sentences[sentenceID];
            const sentence = input.sentence;
            const options = input.options;
            const postfix = input.postfix;

            typewriter(sentence, letterEl)

            setTimeout(() => {
                createButtons(options, postfix)
                window.scrollTo(0, document.body.scrollHeight);
            }, speed * sentence.length)
        }
    }

    const typewriter = (text, contentEl, cb = () => { }) => {
        for (let i = 0; i < text.length; i++) {
            setTimeout(() => {
                contentEl.innerHTML += text.charAt(i);
                window.scrollTo(0, document.body.scrollHeight);
                if (i === text.length - 1) { cb() }
            }, speed * i)
        }
    }

    const createButtons = (options, postfix) => {
        options.map((option) => {
            const btn = document.createElement("button");
            btn.type = 'button';
            btn.innerHTML = option;
            btn.value = option;
            btn.addEventListener('click', function () {
                submitOption(option, postfix);
            }, false);
            optionsEl.appendChild(btn);
        })
    }

    const submitOption = (option, postfix) => {
        const text = option + postfix;
        optionsEl.innerHTML = "";
        const span = document.createElement("span");
        letterEl.append(span);
        typewriter(text, span, nextSentence)

    }

    const nextSentence = () => {
        sentenceID++
        writeLetter()
    }

    writeLetter()

</script>
</html>
